package homework;
//Assignment operator

public class Ass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//member variable
		
		int num1;
		int a=10;
		int b;
		
		//assigning
		
		num1=15;
		b=a;
		
		System.out.println(" answer = " +num1);
		System.out.println(" answer = " +b);

	}

}
