package homework;
//Logical operator

public class Log {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//member variable
		
		int a=10;
		int b=5;
		int c=8;
		boolean d = (a>b)&&(b>c); //and logical operator
		boolean d1 = (a>b)||(b>c);  //or logical operator
		
		System.out.println(" answer = " +d);
		System.out.println(" answer = " +d1);
		

	}

}
